<? /* Template Name: О нас */global $Casco;$isMoscow = get_field('слаг', $Casco->city) == 'Moscow';get_header(); ?>    <main role="main" class="content">        <div class="box-title">            <div class="container">                <div class="page-title"><? the_title() ?></div>            </div>        </div>        <div class="container main-indent">            <div class="row about-page second-indent">                <div class="col-xl-6 col-lg-12">                    <div class="title medium-size extra-bold"><? the_field('заголовок') ?></div>                    <div class="about-content">                        <?                        if ($isMoscow) {                           echo apply_filters('the_content', wpautop(get_post_field('post_content', $id), true));                        } else {                            the_field('контент_немосква');                        }                        ?>                    </div>                    <div class="agent bold">                        <? $isMoscow ? the_field('являемся') : the_field('являемся_немосква') ?>                    </div>                    <div class="mt-5 mb-5">                        <a href="<? bloginfo('url') ?>" class="button btn-yellow">рассчитать полис</a>                    </div>                    <div class="number-contract">                        <? $isMoscow ? the_field('номер_договора') : the_field('номер_немосква') ?>                    </div>                </div>                <div class="col-xl-6 col-lg-12">                    <div class="row certificates">                        <?                        $gallery = get_field('сертификаты');                        if (!empty($gallery)):                            foreach ($gallery as $certificate):                                ?>                                <a class="col-md-4 col-6" href="<?= $certificate['url'] ?>" data-fancybox                                   data-caption="<?= $certificate['caption'] ?>">                                    <img src="<?= $certificate['url'] ?>" class="img-fluid"                                         alt="<?= $certificate['alt'] ?>"/>                                </a>                            <? endforeach;                        endif; ?>                    </div>                </div>            </div>        </div>    </main><? get_footer() ?>