<? /* Template Name: Почему мы */global $Casco;$isMoscow = get_field('слаг', $Casco->city) == 'Moscow';get_header(); ?>    <main role="main" class="content">        <div class="box-title">            <div class="container">                <div class="page-title"><? the_field('заголовок') ?></div>            </div>        </div>        <div class="container main-indent">            <div class="row why-we second-indent">                <? while (have_rows('блоки')): the_row() ?>                    <div class="col-md-6">                        <div class="why-we-item d-flex align-items-start">                            <div class="why-box-img"><img <? repeater_image('иконка') ?> class="img-fluid">                            </div>                            <div>                                <div class="title medium-size"><?                                    if ($isMoscow || (!$isMoscow && !get_sub_field('заголовок_немосква'))) {                                        the_sub_field('заголовок');                                    } elseif (get_sub_field('заголовок_немосква')) {                                        the_sub_field('заголовок_немосква');                                    }                                    ?></div>                                <div><?                                    if ($isMoscow || (!$isMoscow && !get_sub_field('текст_немосква'))) {                                        the_sub_field('текст');                                    } elseif (get_sub_field('текст_немосква')) {                                        the_sub_field('текст_немосква');                                    }                                    ?></div>                            </div>                        </div>                    </div>                <? endwhile; ?>            </div>            <div class="text-center mt-2">                <a href="<? bloginfo('url') ?>" class="button btn-yellow">рассчитать полис</a>            </div>        </div>    </main><? get_footer() ?>